      expect(suite.tests[0].steps).is.ok;
      expect(1610).is.equal(sum);
      done();
    });
  });

  it('should execute scenarios step-by-step ', (done) => {
    printed = [];
    container.append({
      helpers: {
        simple: {
          do(...args) {
            return Promise.resolve().then(() => printed.push(args.join(' ')));
          },
        },
      },
    });
    I = actor();
