          .then(() => {
            const cmd = cy.queue.find({ name: 'window' })

            logs = cmd.get('next').get('logs')

            expect(logs.length).to.eq(3)

            _.each(logs, (log) => {
              expect(log.get('name')).to.eq('xhr')
              expect(log.get('end')).not.to.be.true
