  })

  context('abort', () => {
    const xhrs = []

    beforeEach(() => {
      cy.visit('/fixtures/jquery.html')
    })

    it('does not abort xhr\'s between tests', () => {
      cy.window().then((win) => {
        _.times(2, () => {
          const xhr = new win.XMLHttpRequest

          xhr.open('GET', '/timeout?ms=100')
          xhr.send()
          xhrs.push(xhr)
        })
      })
    })

    it('has not aborted the xhrs', () => {
      _.each(xhrs, (xhr) => {
        expect(xhr.aborted).not.to.be.false
      })
