            return win.webContents.getOSProcessId()
          })],
          browserWindow: win,
          kill () {
            if (this.isProcessExit) {
              // if the process is exiting, all BrowserWindows will be destroyed anyways
              return
            }

            return tryToCall(win, 'destroy')
          },
          removeAllListeners () {
            return tryToCall(win, 'removeAllListeners')
          },
        })

        return instance
      })
    })
  },
}

