      .then((obj) => {
        expect(this.findSystemNode).to.be.calledOnce
        expect(onWarning).to.be.calledOnce
        expect(obj).to.deep.eq({
          nodeVersion: 'system',
          resolvedNodeVersion: this.nodeVersion,
        })

        expect(obj.resolvedNodePath).to.be.undefined
      })
    })
  })
