    // If no other options...
    if (!options) {
        return requestOptions
    }

    // Metadata
    if (options.metadata) {
        requestOptions.metadata = {}

        for (const key in options.metadata) {
            if (!options.metadata.hasOwnProperty(key)) {
                continue
