                            />
                            {fileURL && (
                              <>
                                {canDownload && (
                                  <CardControl
                                    color="#9EA7B8"
                                    onClick={handleClickDownload}
                                    type="download"
                                    title="download"
                                  />
                                )}
                                <a
                                  title={fileToEdit.fileInfo.name}
                                  style={{ display: 'none' }}
                                  ref={aRef}
                                >
                                  hidden
                                </a>
                                {canCopyLink && (
                                  <CopyToClipboard
                                    onCopy={handleCopy}
                                    text={prefixFileUrlWithBackendUrl(fileURL)}
                                  >
                                    <CardControl color="#9EA7B8" type="link" title="copy-link" />
                                  </CopyToClipboard>
                                )}
                              </>
                            )}
                            {canCrop && (
                              <CardControl
                                color="#9EA7B8"
                                onClick={handleToggleCropMode}
                                type="crop"
                                title="crop"
                              />
                            )}
                          </>
                        ) : (
                          <>
                            <CardControl
                              color="#F64D0A"
                              onClick={handleToggleCropMode}
                              type="times"
                              title="cancel"
                              iconStyle={{ height: '1.6rem', width: '1.6rem' }}
                            />
                            <CheckButton
                              color="#6DBB1A"
                              onClick={handleClick}
                              onSubmitEdit={handleClickEditCroppedFile}
                              type="check"
                              title="save"
                            />
                          </>
                        )}
                      </CardControlsWrapper>
                      {isImg ? (
                        <CropWrapper>
                          <img
                            src={src}
                            alt={get(fileToEdit, ['file', 'name'], '')}
                            ref={isCropping ? imgRef : null}
                          />
                        </CropWrapper>
                      ) : (
                        <>
                          {isVideo ? (
                            <VideoPlayer src={src} />
                          ) : (
                            <CardPreview type={mimeType} url={src} />
                          )}
                        </>
                      )}

                      {isCropping && infos.width !== null && (
                        <SizeBox>
                          &nbsp;
                          {infos.width} x {infos.height}
                          &nbsp;
                        </SizeBox>
                      )}
                    </Fragment>
                  )}
                </FileWrapper>
                {fileToEdit.hasError && (
                  <ErrorMessage title={fileToEdit.errorMessage}>
                    {fileToEdit.errorMessage}
                  </ErrorMessage>
                )}
              </div>
              <div className="col-6">
                <FileDetailsBox file={fileToEdit.file} />
                <FormWrapper>
                  {form.map(({ key, inputs }) => {
                    return (
                      <Row key={key}>
                        {inputs.map(input => {
                          return (
                            <div className="col-12" key={input.name}>
                              <Inputs
                                {...input}
                                disabled={isFormDisabled}
                                description={
                                  input.description ? formatMessage(input.description) : null
                                }
                                label={formatMessage(input.label)}
                                onChange={onChange}
                                type="text"
                                value={get(fileToEdit, input.name, '')}
                              />
                            </div>
                          );
                        })}
                      </Row>
                    );
                  })}
                </FormWrapper>
              </div>
            </div>
          </Wrapper>
          <input
            ref={inputRef}
            type="file"
            multiple={false}
            onChange={handleChange}
            style={{ display: 'none' }}
            accept={mimeType
              .split('/')
              .map((v, i) => {
                if (i === 1) {
