import { useEffect, useState } from 'react';
import axios from 'axios';
import { useGlobalContext } from 'strapi-helper-plugin';

const useFetchPluginsFromMarketPlace = () => {
  const { currentLocale } = useGlobalContext();
  const [state, setState] = useState({
    error: false,
    isLoading: true,
    data: null,
  });

  useEffect(() => {
    const CancelToken = axios.CancelToken;
    const source = CancelToken.source();

    const getData = async () => {
      try {
        const { data } = await axios.get('https://marketplace.strapi.io/plugins', {
          cancelToken: source.token,
          params: { lang: currentLocale },
        });

        setState({
          isLoading: false,
          data,
          error: false,
        });
      } catch (err) {
        if (axios.isCancel(err)) {
          // Silent
        } else {
          // handle error
          setState(prev => ({ ...prev, isLoading: false, error: true }));
        }
      }
    };

    getData();

    return () => {
      source.cancel();
    };
  }, [currentLocale]);

  return state;
};

export default useFetchPluginsFromMarketPlace;

import { useCallback, useReducer, useEffect } from 'react';
import { request } from 'strapi-helper-plugin';

import reducer, { initialState } from './reducer';
import { formatPermissionsFromApi } from '../../utils';

const useFetchRole = id => {
  const [state, dispatch] = useReducer(reducer, initialState);

  useEffect(() => {
    if (id) {
      fetchRole(id);
    } else {
      dispatch({
        type: 'GET_DATA_SUCCEEDED',
        role: {},
        permissions: {},
      });
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [id]);

  const fetchRole = async roleId => {
    try {
      const [{ data: role }, { data: permissions }] = await Promise.all(
        [`roles/${roleId}`, `roles/${roleId}/permissions`].map(endPoint =>
          request(`/admin/${endPoint}`, { method: 'GET' })
        )
      );

      dispatch({
        type: 'GET_DATA_SUCCEEDED',
        role,
        permissions: formatPermissionsFromApi(permissions),
      });
    } catch (err) {
      console.error(err);

      dispatch({
        type: 'GET_DATA_ERROR',
      });
      strapi.notification.toggle({
        type: 'warning',
        message: { id: 'notification.error' },
      });
    }
  };

  const handleSubmitSucceeded = useCallback(data => {
    dispatch({
      type: 'ON_SUBMIT_SUCCEEDED',
      ...data,
    });
  }, []);

  return { ...state, onSubmitSucceeded: handleSubmitSucceeded };
};

export default useFetchRole;

