import { useContext, useEffect, useReducer } from 'react';
import { useGlobalContext, hasPermissions, UserContext } from 'strapi-helper-plugin';

import reducer, { initialState } from './reducer';
import init from './init';

const useSettingsMenu = (noCheck = false) => {
  const permissions = useContext(UserContext);
  const { plugins } = useGlobalContext();

  const [{ isLoading, menu }, dispatch] = useReducer(reducer, initialState, () =>
    init(initialState, plugins)
  );

  useEffect(() => {
    const getData = async () => {
      const checkPermissions = async (permissionsToCheck, path) => {
        const hasPermission = await hasPermissions(permissions, permissionsToCheck);

        return { hasPermission, path };
      };

      const generateArrayOfPromises = array => {
        return array.reduce((acc, current, sectionIndex) => {
          const generateArrayOfPromises = array =>
            array.map((link, index) =>
              checkPermissions(array[index].permissions, `${sectionIndex}.links.${index}`)
            );

          return [...acc, ...generateArrayOfPromises(current.links)];
        }, []);
      };

      const generalSectionLinksArrayOfPromises = generateArrayOfPromises(menu);

      const data = await Promise.all(generalSectionLinksArrayOfPromises);

      dispatch({
        type: 'CHECK_PERMISSIONS_SUCCEEDED',
        data,
      });
    };

    // This hook is also used by the main LeftMenu component in order to know which sections it needs to display/hide
    // Therefore, we don't need to make the checking all the times when the hook is used.
    if (!noCheck) {
      getData();
    }

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [permissions, noCheck]);

  return { isLoading, menu };
};

export default useSettingsMenu;

import { useEffect, useReducer } from 'react';
import { request } from 'strapi-helper-plugin';
import { get, has, omit } from 'lodash';
import { checkFormValidity, formatAPIErrors } from '../../utils';
import { initialState, reducer } from './reducer';
import init from './init';

const useUsersForm = (endPoint, schema, cbSuccess, fieldsToPick) => {
  const [
    { formErrors, initialData, isLoading, modifiedData, showHeaderButtonLoader, showHeaderLoader },
    dispatch,
  ] = useReducer(reducer, initialState, () => init(initialState, fieldsToPick));

  useEffect(() => {
    const getData = async () => {
      try {
        const { data } = await request(endPoint, { method: 'GET' });

        dispatch({
          type: 'GET_DATA_SUCCEEDED',
          data,
          fieldsToPick,
        });
      } catch (err) {
        console.error(err.response);
        strapi.notification.toggle({
          type: 'warning',
          message: { id: 'notification.error' },
        });
      }
    };

    if (endPoint) {
      getData();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [endPoint]);

  const handleCancel = () => {
    dispatch({
      type: 'ON_CANCEL',
    });
  };

  const handleChange = ({ target: { name, value, type: inputType } }) => {
    dispatch({
      type: 'ON_CHANGE',
      inputType,
      keys: name,
      value,
    });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    const errors = await checkFormValidity(modifiedData, schema);

    dispatch({
      type: 'SET_ERRORS',
      errors: errors || {},
    });

    if (!errors) {
      try {
        strapi.lockAppWithOverlay();

        dispatch({
          type: 'ON_SUBMIT',
        });

        const cleanedData = omit(modifiedData, ['confirmPassword', 'registrationToken']);

        if (cleanedData.roles) {
          cleanedData.roles = cleanedData.roles.map(role => role.id);
        }

        const { data } = await request(endPoint, {
          method: 'PUT',
          body: cleanedData,
        });

        cbSuccess(data);

        dispatch({
          type: 'ON_SUBMIT_SUCCEEDED',
          data,
        });

        strapi.notification.toggle({
          type: 'success',
          message: { id: 'notification.success.saved' },
        });
      } catch (err) {
        const data = get(err, 'response.payload', { data: {} });

        if (has(data, 'data') && typeof data.data === 'string') {
          strapi.notification.toggle({
            type: 'warning',
            message: data.data,
          });
        } else {
          strapi.notification.toggle({
            type: 'warning',
            message: data.message,
          });
        }

        const apiErrors = formatAPIErrors(data);

        dispatch({
          type: 'SET_ERRORS',
          errors: apiErrors,
        });
      } finally {
        strapi.unlockApp();
      }
    }
  };

  return [
    { formErrors, initialData, isLoading, modifiedData, showHeaderButtonLoader, showHeaderLoader },
    dispatch,
    { handleCancel, handleChange, handleSubmit },
  ];
};

export default useUsersForm;

