'use strict';
const _ = require('lodash');
const domain = require('../../domain/action');
const actionProviderService = require('../permission/action-provider');

describe('Action Provider Service', () => {
  beforeEach(() => {
    global.strapi = {
      plugins: { aPlugin: {} },
    };
  });

  describe('settings', () => {
    const readAction = {
      uid: 'marketplace.read',
      displayName: 'Can read',
      pluginName: 'admin',
      section: 'settings',
      category: 'plugins and marketplace',
      subCategory: 'marketplace',
    };

    const createAction = {
      uid: 'marketplace.create',
      displayName: 'Can create',
      pluginName: 'admin',
      section: 'settings',
      category: 'plugins and marketplace',
    };

    test('Can register a settings action', async () => {
      await actionProviderService.register([readAction]);
      const createdAction = actionProviderService.get(readAction.uid, readAction.pluginName);

      expect(createdAction).toMatchObject({
        ..._.omit(readAction, ['uid']),
        actionId: 'admin::marketplace.read',
      });
    });

    test('Can register a settings action without subCategory', async () => {
      await actionProviderService.register([createAction]);
      const createdAction = actionProviderService.get(createAction.uid, createAction.pluginName);

      expect(createdAction).toMatchObject({
        ..._.omit(createAction, ['uid']),
        actionId: 'admin::marketplace.create',
        subCategory: 'general',
      });
    });

    test('Can get all registered entries (array)', () => {
      expect(actionProviderService.getAll()).toHaveLength(2);
    });

    test('Can get all registered entries (map)', () => {
      expect(actionProviderService.getAllByMap().size).toBe(2);
    });

    test('Can get an action by its actionId', () => {
      const actionId = 'admin::marketplace.create';
      const expected = domain.createAction(createAction);
      expect(actionProviderService.getByActionId(actionId)).toStrictEqual(expected);
    });

    test('Can register a settings action with a pluginName other than "admin"', async () => {
      const action = {
        uid: 'marketplace.update',
        displayName: 'Can update',
        pluginName: 'aPlugin',
        section: 'settings',
        category: 'plugins and marketplace',
      };

      await actionProviderService.register([action]);
      const createdAction = actionProviderService.get(action.uid, action.pluginName);

      expect(createdAction).toMatchObject({
        ..._.omit(action, ['uid']),
        actionId: 'plugins::aPlugin.marketplace.update',
      });
    });

    test('Cannot register a settings action with a non standard name', async () => {
      const action = {
        uid: 'Marketplace Read',
        displayName: 'Access the marketplace',
        pluginName: 'aPlugin',
        section: 'settings',
        category: 'plugins and marketplace',
      };

      expect(() => actionProviderService.register([action])).toThrow(
        '[0].uid: The id can only contain lowercase letters, dots and hyphens.'
      );
    });

    test('Cannot register actions with same actionId', async () => {
      global.strapi.stopWithError = jest.fn(() => {});

      const action1 = {
        uid: 'marketplace.delete',
        displayName: 'Can delete',
        pluginName: 'aPlugin',
        section: 'settings',
        category: 'plugins and marketplace',
      };

      const action2 = {
        uid: action1.uid,
        displayName: 'delete',
        pluginName: 'aPlugin',
        section: 'plugins',
      };

      expect(() => actionProviderService.register([action1, action2])).toThrow(
        'Duplicated action id: plugins::aPlugin.marketplace.delete. You may want to change the actions name.'
      );
    });

    test("Cannot register a settings action with a pluginName that doesn't exist", async () => {
      const action = {
        uid: 'marketplace.read',
        displayName: 'Access the marketplace',
        pluginName: 'plugin-name-that-doesnt-exist',
        section: 'settings',
        category: 'plugins and marketplace',
      };

      expect(() => actionProviderService.register([action])).toThrow(
        '[0].pluginName is not an existing plugin'
      );
    });

    test('Cannot register a settings action without category', async () => {
      const action = {
        uid: 'marketplace.read',
        displayName: 'Access the marketplace',
        pluginName: 'admin',
        section: 'settings',
      };

      expect(() => actionProviderService.register([action])).toThrow(
        '[0].category is a required field'
      );
    });

    test('Cannot register an action outside of the bootstrap function', async () => {
      global.strapi = {
        isLoaded: true,
      };

      expect(() => actionProviderService.register([])).toThrow(
        `You can't register new actions outside of the bootstrap function.`
      );
    });
  });
});

'use strict';

const _ = require('lodash');

const {
  validatePassword,
  hashPassword,
  checkCredentials,
  forgotPassword,
  resetPassword,
} = require('../auth');

describe('Auth', () => {
  describe('checkCredentials', () => {
    test('Fails on not found user, without leaking not found info', async () => {
      const findOne = jest.fn(() => Promise.resolve());

      global.strapi = {
        query() {
          return { findOne };
        },
      };

      const input = { email: 'test@strapi.io', password: 'pcw123' };
      const res = await checkCredentials(input);

      expect(findOne).toHaveBeenCalledWith({ email: input.email });
      expect(res).toEqual([null, false, { message: 'Invalid credentials' }]);
    });

    test('Fails when password is invalid, without leaking specific info', async () => {
      const user = {
        id: 1,
        firstname: '',
        lastname: '',
        email: 'test@strapi.io',
        password: await hashPassword('test-password'),
      };

      const findOne = jest.fn(() => Promise.resolve(user));

      global.strapi = {
        query() {
          return { findOne };
        },
      };

      const input = { email: 'test@strapi.io', password: 'wrong-password' };
      const res = await checkCredentials(input);

      expect(findOne).toHaveBeenCalledWith({ email: input.email });
      expect(res).toEqual([null, false, { message: 'Invalid credentials' }]);
    });

    test.each([false, null, 1, 0])('Fails when user is not active (%s)', async isActive => {
      const user = {
        id: 1,
        firstname: '',
        lastname: '',
        email: 'test@strapi.io',
        isActive,
        password: await hashPassword('test-password'),
      };

      const findOne = jest.fn(() => Promise.resolve(user));

      global.strapi = {
        query() {
          return { findOne };
        },
      };

      const input = { email: 'test@strapi.io', password: 'test-password' };
      const res = await checkCredentials(input);

      expect(findOne).toHaveBeenCalledWith({ email: input.email });
      expect(res).toEqual([null, false, { message: 'User not active' }]);
    });

    test('Returns user when all checks pass', async () => {
      const user = {
        id: 1,
        firstname: '',
        lastname: '',
        email: 'test@strapi.io',
        isActive: true,
        password: await hashPassword('test-password'),
      };

      const findOne = jest.fn(() => Promise.resolve(user));

      global.strapi = {
        query() {
          return { findOne };
        },
      };

      const input = { email: 'test@strapi.io', password: 'test-password' };
      const res = await checkCredentials(input);

      expect(findOne).toHaveBeenCalledWith({ email: input.email });
      expect(res).toEqual([null, user]);
    });
  });

  describe('validatePassword', () => {
    test('Compares password with hash', async () => {
      const password = 'pcw123';
      const hash = await hashPassword(password);

      const isValid = await validatePassword(password, hash);
      expect(isValid).toBe(true);
    });
  });

  describe('forgotPassword', () => {
    test('Only run the process for active users', async () => {
      const findOne = jest.fn(() => Promise.resolve());

      global.strapi = {
        query() {
          return { findOne };
        },
      };

      const input = { email: 'test@strapi.io' };
      await forgotPassword(input);

      expect(findOne).toHaveBeenCalledWith({ email: input.email, isActive: true });
    });

    test('Will return silently in case the user is not found', async () => {
      const findOne = jest.fn(() => Promise.resolve());
      const send = jest.fn(() => Promise.resolve());

      global.strapi = {
        query() {
          return { findOne };
        },
        plugins: {
          email: {
            services: {
              email: { send },
            },
          },
        },
      };

      const input = { email: 'test@strapi.io' };
      await forgotPassword(input);

      expect(findOne).toHaveBeenCalled();
      expect(send).not.toHaveBeenCalled();
    });

    test('Will assign a new reset token', async () => {
      const user = {
        id: 1,
        email: 'test@strapi.io',
      };
      const resetPasswordToken = '123';

      const findOne = jest.fn(() => Promise.resolve(user));
      const send = jest.fn(() => Promise.resolve());
      const updateById = jest.fn(() => Promise.resolve());
      const createToken = jest.fn(() => resetPasswordToken);

      const config = {
        server: {
          host: '0.0.0.0',
        },
        admin: { url: '/admin' },
      };

      global.strapi = {
        config: {
          ...config,
          get(path, def) {
            return _.get(path, def);
          },
        },
        query() {
          return { findOne };
        },
        admin: { services: { user: { updateById }, token: { createToken } } },
        plugins: { email: { services: { email: { send, sendTemplatedEmail: send } } } },
      };

      const input = { email: user.email };
      await forgotPassword(input);

      expect(findOne).toHaveBeenCalled();
      expect(createToken).toHaveBeenCalled();
      expect(updateById).toHaveBeenCalledWith(user.id, { resetPasswordToken });
    });

    test('Will call the send service', async () => {
      const user = {
        id: 1,
        email: 'test@strapi.io',
      };
      const resetPasswordToken = '123';

      const findOne = jest.fn(() => Promise.resolve(user));
      const send = jest.fn(() => Promise.resolve());
      const sendTemplatedEmail = jest.fn(() => Promise.resolve());
      const updateById = jest.fn(() => Promise.resolve());
      const createToken = jest.fn(() => resetPasswordToken);

      const config = {
        server: {
          host: '0.0.0.0',
          admin: { url: '/admin', forgotPassword: { emailTemplate: {} } },
        },
      };

      global.strapi = {
        config: {
          ...config,
          get(path, def) {
            return _.get(path, def);
          },
        },
        query() {
          return { findOne };
        },
        admin: {
          services: {
            user: { updateById },
            token: { createToken },
          },
        },
        plugins: { email: { services: { email: { send, sendTemplatedEmail } } } },
      };

      const input = { email: user.email };
      await forgotPassword(input);

      expect(findOne).toHaveBeenCalled();
      expect(createToken).toHaveBeenCalled();
      expect(sendTemplatedEmail).toHaveBeenCalled();
    });
  });

  describe('resetPassword', () => {
    test('Check user is active', async () => {
      const resetPasswordToken = '123';
      const findOne = jest.fn(() => Promise.resolve());
      const badRequest = jest.fn(() => {});

      global.strapi = {
        query() {
          return { findOne };
        },
        errors: { badRequest },
      };

      expect.assertions(2);
      return resetPassword({ resetPasswordToken, password: 'Test1234' }).catch(() => {
        expect(findOne).toHaveBeenCalledWith({ resetPasswordToken, isActive: true });
        expect(badRequest).toHaveBeenCalled();
      });
    });

    test('Fails if user is not found', async () => {
      const resetPasswordToken = '123';
      const findOne = jest.fn(() => Promise.resolve());
      const badRequest = jest.fn(() => {});

      global.strapi = {
        query() {
          return { findOne };
        },
        errors: { badRequest },
      };

      expect.assertions(1);
      return resetPassword({ resetPasswordToken, password: 'Test1234' }).catch(() => {
        expect(badRequest).toHaveBeenCalled();
      });
    });

    test('Changes password and clear reset token', async () => {
      const resetPasswordToken = '123';
      const user = { id: 1 };

      const findOne = jest.fn(() => Promise.resolve(user));
      const updateById = jest.fn(() => Promise.resolve());

      global.strapi = {
        query() {
          return { findOne };
        },
        admin: { services: { user: { updateById } } },
      };

      const input = { resetPasswordToken, password: 'Test1234' };
      await resetPassword(input);

      expect(updateById).toHaveBeenCalledWith(user.id, {
        password: input.password,
        resetPasswordToken: null,
      });
    });
  });
});

'use strict';

const _ = require('lodash');
const permissionService = require('../permission');

describe('Permission Service', () => {
  describe('Find permissions', () => {
    test('Find calls the right db query', async () => {
      const find = jest.fn(() => Promise.resolve([]));
      global.strapi = {
        query() {
          return { find };
        },
      };

      await permissionService.find({ role: 1 });

      expect(find).toHaveBeenCalledWith({ role: 1 }, []);
    });
  });

  describe('Find User Permissions', () => {
    test('Find calls the right db query', async () => {
      const find = jest.fn(({ role_in }) => role_in);

      global.strapi = {
        query() {
          return { find };
        },
      };

      const rolesId = [1, 2];

      const res = await permissionService.findUserPermissions({
        roles: rolesId.map(id => ({ id })),
      });

      expect(find).toHaveBeenCalledWith({ role_in: rolesId, _limit: -1 });
      expect(res).toStrictEqual(rolesId);
    });

    test('Returns default result when no roles provided', async () => {
      const res = await permissionService.findUserPermissions({});

      expect(res).toStrictEqual([]);
    });
  });

  describe('Sanitize Permission', () => {
    test('Removes unwanted properties', () => {
      const removeUnkownConditionIds = jest.fn(() => ['cond']);
      global.strapi = {
        admin: { services: { condition: { removeUnkownConditionIds } } },
      };
      const permission = {
        action: 'read',
        subject: 'article',
        fields: ['*'],
        conditions: ['cond', 'unknown-cond'],
        foo: 'bar',
      };

      const sanitizedPermission = permissionService.sanitizePermission(permission);

      expect(sanitizedPermission.foo).toBeUndefined();
      expect(sanitizedPermission).toMatchObject({
        ..._.omit(permission, 'foo'),
        conditions: ['cond'],
      });
    });
  });

  describe('cleanPermissionInDatabase', () => {
    test('Remove permission that dont exist + clean fields', async () => {
      const permsInDb = [
        {
          id: 1,
          action: 'action-1',
          fields: ['name'],
        },
        {
          id: 2,
          action: 'action-2',
          fields: ['name'],
        },
        {
          id: 3,
          action: 'action-3',
          subject: 'country',
          fields: ['name'],
        },
        {
          id: 4,
          action: 'action-3',
          subject: 'planet',
          fields: ['name'],
        },
        {
          id: 5,
          action: 'action-1',
          subject: 'planet',
          fields: ['name', 'description'],
        },
        {
          id: 6,
          action: 'action-1',
          subject: 'country',
          fields: null,
        },
      ];

      const permsWithCleanFields = [
        permsInDb[0],
        permsInDb[2],
        { ...permsInDb[4], fields: ['name', 'galaxy'] },
        { ...permsInDb[5], fields: ['name'] },
      ];

      const findPage = jest.fn(() =>
        Promise.resolve({
          results: permsInDb,
          pagination: { total: 4 },
        })
      );
      const cleanPermissionFields = jest.fn(() => permsWithCleanFields);
      const dbDelete = jest.fn(() => Promise.resolve());
      const update = jest.fn(() => Promise.resolve());
      const registeredPerms = new Map();
      registeredPerms.set('action-1', {});
      registeredPerms.set('action-3', { subjects: ['country'] });
      const getAllByMap = jest.fn(() => registeredPerms);
      const prevGetAllByMap = permissionService.actionProvider.getAllByMap;
      permissionService.actionProvider.getAllByMap = getAllByMap;

      global.strapi = {
        query: () => ({ findPage, delete: dbDelete, update }),
        admin: { services: { 'content-type': { cleanPermissionFields } } },
      };

      await permissionService.cleanPermissionInDatabase();

      expect(findPage).toHaveBeenCalledWith({ page: 1, pageSize: 200 }, []);
      expect(update).toHaveBeenNthCalledWith(1, { id: permsInDb[4].id }, permsWithCleanFields[2]);
      expect(update).toHaveBeenNthCalledWith(2, { id: permsInDb[5].id }, permsWithCleanFields[3]);
      expect(getAllByMap).toHaveBeenCalledWith();
      expect(dbDelete).toHaveBeenCalledWith({ id_in: [2, 4] });

      // restauring actionProvider
      permissionService.actionProvider.getAllByMap = prevGetAllByMap;
    });
  });
});

'use strict';

const _ = require('lodash');
const roleService = require('../role');
const { SUPER_ADMIN_CODE } = require('../constants');

describe('Role', () => {
  describe('create', () => {
    test('Creates a role', async () => {
      const dbCreate = jest.fn(role => Promise.resolve(role));
      const dbCount = jest.fn(() => Promise.resolve(0));

      global.strapi = {
        query: () => ({ create: dbCreate, count: dbCount }),
      };

      const input = {
        name: 'super_admin',
        description: "Have all permissions. Can't be delete",
        code: 'super-admin',
      };

      const createdRole = await roleService.create(input);

      expect(dbCreate).toHaveBeenCalledWith(input);
      expect(createdRole).toStrictEqual(input);
    });
  });
  describe('findOne', () => {
    test('Finds a role', async () => {
      const role = {
        id: 1,
        name: 'super_admin',
        description: "Have all permissions. Can't be delete",
      };
      const dbFindOne = jest.fn(({ id }) => Promise.resolve(_.find([role], { id })));

      global.strapi = {
        query: () => ({ findOne: dbFindOne }),
      };

      const foundRole = await roleService.findOne({ id: role.id });

      expect(dbFindOne).toHaveBeenCalledWith({ id: role.id }, []);
      expect(foundRole).toStrictEqual(role);
    });
    test('Finds a role with usersCount', async () => {
      const role = {
        id: 1,
        name: 'super_admin',
        description: "Have all permissions. Can't be delete",
        usersCount: 0,
      };
      const dbFindOne = jest.fn(({ id }) =>
        Promise.resolve(_.find([_.omit(role, ['usersCount'])], { id }))
      );
      const dbCount = jest.fn(() => Promise.resolve(0));
      global.strapi = {
        query: () => ({ findOne: dbFindOne, count: dbCount }),
      };

      const foundRole = await roleService.findOneWithUsersCount({ id: role.id });

      expect(dbFindOne).toHaveBeenCalledWith({ id: role.id }, []);
      expect(dbCount).toHaveBeenCalledWith({ roles: [role.id] });
      expect(foundRole).toStrictEqual(role);
    });
  });
  describe('find', () => {
    test('Finds roles', async () => {
      const roles = [
        {
          id: 1,
          name: 'super_admin',
          description: "Have all permissions. Can't be delete",
        },
      ];
      const dbFind = jest.fn(() => Promise.resolve(roles));

      global.strapi = {
        query: () => ({ find: dbFind }),
      };

      const foundRoles = await roleService.find();

      expect(dbFind).toHaveBeenCalledWith({}, []);
      expect(foundRoles).toStrictEqual(roles);
    });
  });
  describe('findAll', () => {
    test('Finds all roles', async () => {
      const roles = [
        {
          id: 1,
          name: 'super_admin',
          description: "Have all permissions. Can't be delete",
          usersCount: 0,
        },
      ];
      const dbFind = jest.fn(() =>
        Promise.resolve(roles.map(role => _.omit(role, ['usersCount'])))
      );
      const dbCount = jest.fn(() => Promise.resolve(0));

      global.strapi = {
        query: () => ({ find: dbFind, count: dbCount }),
      };

      const foundRoles = await roleService.findAllWithUsersCount();

      expect(dbFind).toHaveBeenCalledWith({ _limit: -1 }, []);
      expect(foundRoles).toStrictEqual(roles);
    });
  });
  describe('update', () => {
    test('Updates a role', async () => {
      const role = {
        id: 1,
        name: 'super_admin',
        description: 'AAA',
      };
      const expectedUpdatedRole = {
        id: 1,
        name: 'super_admin_updated',
        description: 'AAA_updated',
      };
      const dbUpdate = jest.fn(() => Promise.resolve(expectedUpdatedRole));
      const dbCount = jest.fn(() => Promise.resolve(0));

      global.strapi = {
        query: () => ({ update: dbUpdate, count: dbCount }),
      };

      const updatedRole = await roleService.update(
        {
          id: role.id,
        },
        {
          name: expectedUpdatedRole.name,
          description: expectedUpdatedRole.description,
        }
      );

      expect(dbUpdate).toHaveBeenCalledWith(
        {
          id: role.id,
        },
        {
          name: expectedUpdatedRole.name,
          description: expectedUpdatedRole.description,
        }
      );
      expect(updatedRole).toStrictEqual(expectedUpdatedRole);
    });
    test('Cannot update code of super admin role', async () => {
      const dbUpdate = jest.fn();
      const dbFind = jest.fn(() => [{ id: '1' }]);
      const dbFindOne = jest.fn(() => ({ id: '1', code: SUPER_ADMIN_CODE }));
      const badRequest = jest.fn(() => {});

      global.strapi = {
        query: () => ({ find: dbFind, findOne: dbFindOne, update: dbUpdate }),
        admin: { config: { superAdminCode: SUPER_ADMIN_CODE } },
        errors: { badRequest },
