  test.each(['24:11:23', '23:72:11', '12:45:83', 1234, {}, 'test', new Date()])(
    'Throws on invalid time (%s)',
    async input => {
      const res = await rq.post(
        '/content-manager/collection-types/application::withtime.withtime',
