const path = require('path');
const fse = require('fs-extra');
const _ = require('lodash');

/**
 * Deletes the API folder of a contentType
 * @param {string} uid content type uid
 */
async function clear(uid) {
  const { apiName, __filename__ } = strapi.contentTypes[uid];

  const apiFolder = path.join(strapi.dir, 'api', apiName);

  // base name of the model file that will be use as comparator
  const baseName = path.basename(__filename__, '.settings.json');

  await recursiveRemoveFiles(apiFolder, createDeleteApiFunction(baseName));
  await deleteBackup(uid);
}

/**
 * Backups the API folder of a contentType
 * @param {string} uid content type uid
 */
async function backup(uid) {
  const { apiName } = strapi.contentTypes[uid];

  const apiFolder = path.join(strapi.dir, 'api', apiName);
  const backupFolder = path.join(strapi.dir, 'api', '.backup', apiName);

  // backup the api folder
  await fse.copy(apiFolder, backupFolder);
}

/**
 * Deletes an API backup folder
 * @param {string} uid content type uid
 */
async function deleteBackup(uid) {
  const { apiName } = strapi.contentTypes[uid];

  const backupFolder = path.join(strapi.dir, 'api', '.backup');
  const apiBackupFolder = path.join(strapi.dir, 'api', '.backup', apiName);

  await fse.remove(apiBackupFolder);

  const list = await fse.readdir(backupFolder);
  if (list.length === 0) {
    await fse.remove(backupFolder);
  }
}

/**
 * Rollbacks the API folder of a contentType
 * @param {string} uid content type uid
 */
async function rollback(uid) {
  const { apiName } = strapi.contentTypes[uid];

  const apiFolder = path.join(strapi.dir, 'api', apiName);
  const backupFolder = path.join(strapi.dir, 'api', '.backup', apiName);

  const exists = await fse.exists(backupFolder);

  if (!exists) {
    throw new Error('Cannot rollback api that was not backed up');
  }

  await fse.remove(apiFolder);
  await fse.copy(backupFolder, apiFolder);
  await deleteBackup(uid);
}

/**
 * Creates a delete function to clear an api folder
 * @param {string} baseName
 */
const createDeleteApiFunction = baseName => {
  const startWithBaseName = startWithName(baseName + '.');

  /**
   * Delets a file in an api.
   * Will only update routes.json instead of deleting it if other routes are present
   * @param {string} filePath file path to delete
   */
  return async filePath => {
    const fileName = path.basename(filePath);

    if (startWithBaseName(fileName)) return fse.remove(filePath);

    if (fileName === 'routes.json') {
      const { routes } = await fse.readJSON(filePath);

      const routesToKeep = routes.filter(route => !startWithBaseName(route.handler));

      if (routesToKeep.length === 0) {
        return fse.remove(filePath);
      }

      await fse.writeJSON(
        filePath,
        {
          routes: routesToKeep,
        },
        {
          spaces: 2,
        }
      );
    }
  };
};

/**
 * Returns a function that checks if the passed string starts with the name
 * @param {string} prefix
 * @returns {Function} a comparing function
 */
const startWithName = prefix => {
  /**
   * Checks if str starts with prefix case insensitive
   * @param {string} str string to compare
   */
  return str => _.startsWith(_.toLower(str), _.toLower(prefix));
};

/**
 * Deletes a folder recursively using a delete function
 * @param {string} folder folder to delete
 * @param {Function} deleteFn function to call with the file path to delete
 */
const recursiveRemoveFiles = async (folder, deleteFn) => {
  const filesName = await fse.readdir(folder);

  for (const fileName of filesName) {
    const filePath = path.join(folder, fileName);

    const stat = await fse.stat(filePath);

    if (stat.isDirectory()) {
      await recursiveRemoveFiles(filePath, deleteFn);
    } else {
      await deleteFn(filePath);
    }
  }

  const files = await fse.readdir(folder);
  if (files.length === 0) {
    await fse.remove(folder);
  }
};

module.exports = {
  clear,
  backup,
  rollback,
};

'use strict';

const path = require('path');
const fse = require('fs-extra');
const _ = require('lodash');
const { toUID, isConfigurable } = require('../../utils/attributes');

module.exports = function createSchemaHandler(infos) {
  const { category, modelName, plugin, uid, dir, filename, schema } = infos;

  const initialState = {
    modelName,
    plugin,
    category,
    uid,
    dir,
    filename,
    schema: schema || {
      info: {},
      options: {},
      attributes: {},
    },
  };

  const state = _.cloneDeep(initialState);

  // always keep it the same to rollback
  Object.freeze(initialState.schema);

  let modified = false;
  let deleted = false;

  return {
    get modelName() {
      return initialState.modelName;
    },

    get plugin() {
      return initialState.plugin;
    },

    get category() {
      return initialState.category;
    },

    get kind() {
      return _.get(state.schema, 'kind', 'collectionType');
    },

    get uid() {
      return state.uid;
    },

    get writable() {
      return _.get(state, 'plugin') !== 'admin';
    },

    setUID(val) {
      modified = true;

      state.uid = val;
      return this;
    },

    setDir(val) {
      modified = true;

      state.dir = val;
      return this;
    },

    get schema() {
      return _.cloneDeep(state.schema);
    },

    setSchema(val) {
      modified = true;

      state.schema = _.cloneDeep(val);
      return this;
    },

    // get a particuar path inside the schema
    get(path) {
      return _.get(state.schema, path);
    },

    // set a particuar path inside the schema
    set(path, val) {
      modified = true;

      const value = _.defaultTo(val, _.get(state.schema, path));
      _.set(state.schema, path, value);

      return this;
    },

    // delete a particuar path inside the schema
    unset(path) {
      modified = true;

      _.unset(state.schema, path);

      return this;
    },

    delete() {
      deleted = true;
      return this;
    },

    getAttribute(key) {
      return this.get(['attributes', key]);
    },

    setAttribute(key, attribute) {
      return this.set(['attributes', key], attribute);
    },

    deleteAttribute(key) {
      return this.unset(['attributes', key]);
    },

    setAttributes(newAttributes) {
      // delete old configurable attributes
      for (let key in this.schema.attributes) {
        if (isConfigurable(this.schema.attributes[key])) {
          this.deleteAttribute(key);
        }
      }

      // set new Attributes
      for (let key in newAttributes) {
        this.setAttribute(key, newAttributes[key]);
      }

      return this;
    },

    removeContentType(uid) {
      const { attributes } = state.schema;

      Object.keys(attributes).forEach(key => {
        const attr = attributes[key];
        const target = attr.model || attr.collection;
        const plugin = attr.plugin;

        const relationUID = toUID(target, plugin);

        if (relationUID === uid) {
          this.deleteAttribute(key);
        }
      });

      return this;
    },

    // utils
    removeComponent(uid) {
      const { attributes } = state.schema;

      Object.keys(attributes).forEach(key => {
        const attr = attributes[key];

        if (attr.type === 'component' && attr.component === uid) {
          this.deleteAttribute(key);
        }

        if (
          attr.type === 'dynamiczone' &&
          Array.isArray(attr.components) &&
          attr.components.includes(uid)
        ) {
          const updatedComponentList = attributes[key].components.filter(val => val !== uid);
          this.set(['attributes', key, 'components'], updatedComponentList);
        }
      });

      return this;
    },

    updateComponent(uid, newUID) {
      const { attributes } = state.schema;

      Object.keys(attributes).forEach(key => {
        const attr = attributes[key];

        if (attr.type === 'component' && attr.component === uid) {
          this.set(['attributes', key, 'component'], newUID);
        }

        if (
          attr.type === 'dynamiczone' &&
          Array.isArray(attr.components) &&
          attr.components.includes(uid)
        ) {
          const updatedComponentList = attr.components.map(val => (val === uid ? newUID : val));

          this.set(['attributes', key, 'components'], updatedComponentList);
        }
      });

      return this;
    },

    // save the schema to disk
    async flush() {
      if (!this.writable) {
        return;
      }

      const initialPath = path.join(initialState.dir, initialState.filename);
      const filePath = path.join(state.dir, state.filename);

      if (deleted === true) {
        await fse.remove(initialPath);

        const list = await fse.readdir(initialState.dir);
        if (list.length === 0) {
          await fse.remove(initialState.dir);
        }

        return;
      }

      if (modified === true) {
        await fse.ensureFile(filePath);

        await fse.writeJSON(
          filePath,
          {
            kind: state.schema.kind,
            connection: state.schema.connection,
            collectionName: state.schema.collectionName,
            info: state.schema.info,
            options: state.schema.options,
            attributes: state.schema.attributes,
          },
          { spaces: 2 }
        );

        // remove from oldPath
        if (initialPath !== filePath) {
          await fse.remove(initialPath);

          const list = await fse.readdir(initialState.dir);
          if (list.length === 0) {
            await fse.remove(initialState.dir);
          }
        }

        return;
      }

      return Promise.resolve();
    },

    // reset the schema to its initial value
    async rollback() {
      if (!this.writable) {
        return;
      }

      const initialPath = path.join(initialState.dir, initialState.filename);
      const filePath = path.join(state.dir, state.filename);

      // it was a creation so it needs to be deleted
      if (!initialState.uid) {
        await fse.remove(filePath);

        const list = await fse.readdir(state.dir);
        if (list.length === 0) {
          await fse.remove(state.dir);
        }
        return;
      }

      if (modified === true || deleted === true) {
        await fse.ensureFile(initialPath);
        await fse.writeJSON(initialPath, initialState.schema, { spaces: 2 });

        // remove
        if (initialPath !== filePath) {
          await fse.remove(filePath);

          const list = await fse.readdir(state.dir);
          if (list.length === 0) {
            await fse.remove(state.dir);
          }
        }
      }

      return Promise.resolve();
    },
  };
};

'use strict';

const { createAuthRequest } = require('../../../test/helpers/request');
const { createStrapiInstance } = require('../../../test/helpers/strapi');

let strapi;
let rq;

const restart = async () => {
  await strapi.destroy();
  strapi = await createStrapiInstance();
  rq = await createAuthRequest({ strapi });
};

describe('Content Type Builder - Components', () => {
  beforeAll(async () => {
    strapi = await createStrapiInstance();
    rq = await createAuthRequest({ strapi });
  }, 60000);

  afterAll(async () => {
    await strapi.destroy();
  });

  describe('POST /components', () => {
    test('Validates input and return 400 in case of invalid input', async () => {
      const res = await rq({
        method: 'POST',
        url: '/content-type-builder/components',
        body: {
          component: {},
        },
      });

      expect(res.statusCode).toBe(400);
      expect(res.body).toEqual({
        error: {
          'component.category': ['category.required'],
          'component.icon': ['icon.required'],
          'component.attributes': ['attributes.required'],
          'component.name': ['name.required'],
        },
      });
    });

    test('Creates a component properly', async () => {
      const res = await rq({
        method: 'POST',
        url: '/content-type-builder/components',
        body: {
          component: {
            category: 'default',
            icon: 'default',
            name: 'Some Component',
            attributes: {
              title: {
                type: 'string',
              },
              pic: {
                type: 'media',
              },
            },
          },
        },
      });

      expect(res.statusCode).toBe(201);
      expect(res.body).toEqual({
        data: {
          uid: 'default.some-component',
        },
      });

      await restart();
    }, 60000);

    test('Errors on already existing components', async () => {
      const res = await rq({
        method: 'POST',
        url: '/content-type-builder/components',
        body: {
          component: {
            category: 'default',
            icon: 'default',
            name: 'someComponent',
            attributes: {},
          },
        },
      });

      expect(res.statusCode).toBe(400);
      expect(res.body).toEqual({
        error: 'component.alreadyExists',
      });
    });
  });

  describe('Get /components', () => {
    test('Returns valid enveloppe', async () => {
      const res = await rq({
        method: 'GET',
        url: '/content-type-builder/components',
      });

      expect(res.statusCode).toBe(200);
      expect(res.body).toMatchObject({
        data: expect.any(Array),
      });

      res.body.data.forEach(el => {
        expect(el).toMatchObject({
          uid: expect.any(String),
          schema: expect.objectContaining({
            name: expect.any(String),
            description: expect.any(String),
            connection: expect.any(String),
            collectionName: expect.any(String),
            attributes: expect.objectContaining({}),
          }),
        });
      });
    });
  });

  describe('GET /components/:uid', () => {
    test('Returns 404 on not found', async () => {
      const res = await rq({
        method: 'GET',
        url: '/content-type-builder/components/nonexistent-component',
      });

      expect(res.statusCode).toBe(404);
      expect(res.body).toEqual({
        error: 'component.notFound',
      });
    });

    test('Returns correct format', async () => {
      const res = await rq({
        method: 'GET',
        url: '/content-type-builder/components/default.some-component',
      });

      expect(res.statusCode).toBe(200);
      expect(res.body).toMatchObject({
        data: {
          uid: 'default.some-component',
          category: 'default',
          schema: {
            icon: 'default',
            name: 'Some Component',
            description: '',
            connection: 'default',
            collectionName: 'components_default_some_components',
            attributes: {
              title: {
                type: 'string',
              },
              pic: {
                type: 'media',
                multiple: false,
                required: false,
              },
            },
          },
        },
      });
    });
  });

  describe('PUT /components/:uid', () => {
    test('Throws 404 on updating non existent component', async () => {
      const res = await rq({
        method: 'PUT',
        url: '/content-type-builder/components/nonexistent-components',
      });

      expect(res.statusCode).toBe(404);
      expect(res.body).toEqual({
        error: 'component.notFound',
      });
    });

    test('Validates input and return 400 in case of invalid input', async () => {
      const res = await rq({
        method: 'PUT',
        url: '/content-type-builder/components/default.some-component',
        body: {
          component: {
            attributes: {},
          },
        },
      });

      expect(res.statusCode).toBe(400);
      expect(res.body).toEqual({
        error: {
          'component.category': ['category.required'],
          'component.icon': ['icon.required'],
          'component.name': ['name.required'],
        },
      });
    });

    test('Updates a component properly', async () => {
      const res = await rq({
        method: 'PUT',
        url: '/content-type-builder/components/default.some-component',
        body: {
          component: {
            category: 'default',
            icon: 'default',
            name: 'New Component',
            attributes: {},
          },
        },
      });

      expect(res.statusCode).toBe(200);
      expect(res.body).toEqual({
        data: {
          uid: 'default.some-component',
        },
      });

      await restart();

      const getRes = await rq({
        method: 'GET',
        url: '/content-type-builder/components/default.some-component',
      });

      expect(getRes.statusCode).toBe(200);
      expect(getRes.body).toMatchObject({
        data: {
          uid: 'default.some-component',
          schema: {
            name: 'New Component',
          },
        },
      });
    }, 60000);
  });

  describe('DELETE /components/:uid', () => {
    test('Throws 404 on non existent component', async () => {
      const res = await rq({
        method: 'DELETE',
        url: '/content-type-builder/components/nonexistent-components',
      });

      expect(res.statusCode).toBe(404);
      expect(res.body).toEqual({
        error: 'component.notFound',
      });
    });

    test('Deletes a component correctly', async () => {
      const res = await rq({
        method: 'DELETE',
        url: '/content-type-builder/components/default.some-component',
      });

      expect(res.statusCode).toBe(200);
      expect(res.body).toEqual({
        data: {
          uid: 'default.some-component',
        },
      });

      await restart();

      const tryGet = await rq({
        method: 'GET',
        url: '/content-type-builder/components/default.some-component',
      });

      expect(tryGet.statusCode).toBe(404);
      expect(tryGet.body).toEqual({
        error: 'component.notFound',
      });
    }, 60000);
  });
});

/**
