          id: data.file.id,
          name: 'test name',
          alternativeText: 'alternative text test',
          caption: 'caption test',
        },
      },
    });
  });

  test('Delete a file', async () => {
    const res = await rq({
      url: '/graphql',
      method: 'POST',
      body: {
        query: /* GraphQL */ `
          mutation removeFile($id: ID!) {
            deleteFile(input: { where: { id: $id } }) {
              file {
                id
              }
            }
          }
        `,
        variables: {
          id: data.file.id,
        },
      },
