const google = 'https://www.google.com';
const axios = require('axios');

(async () => {
	const resp = await axios.get(google);
	console.log('axios done: '); console.log(resp);
})();

const got = require('got');

(async () => {
	const resp = await got(google);
	console.log('got done: '); console.log(resp);
})();

const superagent = require('superagent');

(async () => {
	const resp = await superagent.get(google);
	console.log('superagent done: '); console.log(resp);
})();

const fetch = require('node-fetch');

(async () => {
	const resp = await fetch(google);
	console.log('node-fetch done: '); console.log(resp);
})();

