});

test("getEncodingFromAlias invalid", (t) => {
	const error = t.throws(function() {
		nonAsciiEscaper.getEncodingFromAlias("asd");
	});
	t.is(error.message, `Encoding "asd" is not supported. Only UTF-8, ISO-8859-1 are allowed values`);
